/* ---------- helpers ---------- */
function getYearFromString(dedicatedStr) {
  const m = String(dedicatedStr).match(/(\d{4})/);
  return m ? parseInt(m[1], 10) : NaN;
}

/* ---------- DOM refs ---------- */
const container = document.getElementById('templeContainer');
const navLinks = Array.from(document.querySelectorAll('.navigation a'));
const menuBtn = document.getElementById('menu');
const nav = document.querySelector('.navigation');

/* ---------- render ---------- */
function createCard(temple) {
  const card = document.createElement('article');
  card.className = 'card';
  card.innerHTML = `
    <img src="${temple.imageUrl}" alt="${temple.templeName}" loading="lazy">
    <div class="card-body">
      <h3>${temple.templeName}</h3>
      <p><strong>Location:</strong> ${temple.location}</p>
      <p><strong>Dedicated:</strong> ${temple.dedicated}</p>
      <p><strong>Area:</strong> ${temple.area.toLocaleString()} sq ft</p>
    </div>
  `;
  return card;
}

function displayTemples(list) {
  try {
    container.innerHTML = '';
    if (!list || list.length === 0) {
      container.innerHTML = '<p>No temples match that filter.</p>';
      return;
    }
    const frag = document.createDocumentFragment();
    list.forEach(t => frag.appendChild(createCard(t)));
    container.appendChild(frag);
  } catch (err) {
    console.error('Error building temple cards:', err);
    container.innerHTML = '<p>Unexpected error rendering temples. See console for details.</p>';
  }
}

/* ---------- filtering ---------- */
function applyFilter(filter) {
  let out;
  switch (filter) {
    case 'old':
      out = temples.filter(t => getYearFromString(t.dedicated) < 1900);
      break;
    case 'new':
      out = temples.filter(t => getYearFromString(t.dedicated) > 2000);
      break;
    case 'large':
      out = temples.filter(t => Number(t.area) > 90000);
      break;
    case 'small':
      out = temples.filter(t => Number(t.area) < 10000);
      break;
    default:
      out = temples.slice();
  }
  displayTemples(out);
}

/* ---------- nav behavior ---------- */
navLinks.forEach(a => {
  a.addEventListener('click', (e) => {
    e.preventDefault();
    const f = a.dataset.filter || 'all';
    // visually mark active
    navLinks.forEach(n => n.classList.remove('active'));
    a.classList.add('active');
    // close mobile nav if open
    nav.classList.remove('open');
    menuBtn.innerText = '☰';
    applyFilter(f);
  });
});

/* hamburger toggle (mobile) */
menuBtn.addEventListener('click', () => {
  nav.classList.toggle('open');
  menuBtn.innerText = nav.classList.contains('open') ? '×' : '☰';
});

/* ---------- footer dates ---------- */
document.getElementById('currentYear').textContent = new Date().getFullYear();
document.getElementById('lastModified').textContent = document.lastModified || 'Unknown';

/* ---------- initial render ---------- */
displayTemples(temples);

// mark Home active initially
const homeLink = document.querySelector('.navigation a[data-filter="all"]');
if (homeLink) homeLink.classList.add('active');

